package loop;

public class TriangleNestedloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//i for rows and j for columns
		int i,j;
		
			for(i=1;i<=5;i++) {
				for(j=1;j<=i;j++) {
					// to print stars
			System.out.println("*");
			}
			//throws the cursor in a new line 
			
		 
			 System.out.println();	
		
		 }
			}
}
	
	
	


	
	


